import React,{ Component } from 'react';
import './App.css';
import ColorBox from './ColorBox';


class App extends Component {
    constructor(props){
        super(props)
            this.state = [<ColorBox />]
    }

    add = () => {
      let newElements = this.state
      newElements.push(<ColorBox />)

      this.setState(newElements)
    }

    render() {


      return (
        <div class = "buttons">
          <button onClick = {this.add}>Add</button>
        </div>
      );
    }
}

export default App;
